<script setup lang="ts">


import {computed} from "vue";
import hashgraph from "@hashgraph/proto";
import {arrayify} from "ethers/lib/utils";

defineProps<{
  msg: string
}>()

const key = ""

const decodedKey = computed(() => {
  let result: hashgraph.proto.Key|null
  const keyByteArray = arrayify(key)
  try {
    result = hashgraph.proto.Key.decode(keyByteArray)
  } catch(reason) {
    console.warn("Failed to decode key:" + reason)
    result = null
  }
  return result
})

const status = computed(() => {
  return decodedKey.value !== null ? JSON.stringify(decodedKey.value) : "Key not decoded"
})

</script>

<template>
  <div class="greetings">
    <h1 class="green">{{ status }}</h1>
    <h3>
